// client/src/components/tasks/TaskList.js
import React, { useContext, useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import { TaskContext } from '../../context/TaskContext';
import { StatusContext } from '../../context/StatusContext';
import TaskItem from './TaskItem';
import Spinner from '../layout/Spinner';

const TaskList = () => {
  const taskContext = useContext(TaskContext);
  const statusContext = useContext(StatusContext);

  const { tasks, loading, getTasks } = taskContext;
  const { statuses, getStatuses } = statusContext;

  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');
  const [sortBy, setSortBy] = useState('deadline');

  useEffect(() => {
    getTasks();
    getStatuses();
    // eslint-disable-next-line
  }, []);

  // Настройка периодического обновления списка задач каждые 30 секунд
  useEffect(() => {
    const intervalId = setInterval(() => {
      getTasks();
    }, 30000); // 30 секунд

    return () => clearInterval(intervalId); // Очистка интервала при размонтировании
    // eslint-disable-next-line
  }, []);

  if (loading) {
    return <Spinner />;
  }

  const groupTasksByStatus = () => {
    const groupedTasks = {};

    // Инициализация групп для всех статусов
    if (statuses) {
      statuses.forEach(status => {
        groupedTasks[status._id] = {
          status,
          tasks: []
        };
      });

      // Фильтрация и сортировка задач
      let filteredTasks = tasks || [];

      // Применение поиска
      if (searchTerm) {
        const term = searchTerm.toLowerCase();
        filteredTasks = filteredTasks.filter(task => 
          (task.title && task.title.toLowerCase().includes(term)) || 
          (task.description && task.description.toLowerCase().includes(term))
        );
      }

      // Применение фильтра по категории
      if (filterCategory !== 'all') {
        filteredTasks = filteredTasks.filter(task => 
          task.category === filterCategory
        );
      }

      // Применение сортировки
      switch (sortBy) {
        case 'deadline':
          filteredTasks.sort((a, b) => {
            if (!a.dueDate) return 1;
            if (!b.dueDate) return -1;
            return new Date(a.dueDate) - new Date(b.dueDate);
          });
          break;
        case 'priority':
          const priorityOrder = { high: 1, medium: 2, low: 3 };
          filteredTasks.sort((a, b) => 
            priorityOrder[a.priority || 'medium'] - priorityOrder[b.priority || 'medium']
          );
          break;
        case 'created':
          filteredTasks.sort((a, b) => 
            new Date(b.createdAt) - new Date(a.createdAt)
          );
          break;
        default:
          break;
      }

      // Распределение задач по группам
      filteredTasks.forEach(task => {
        if (task.status && groupedTasks[task.status]) {
          groupedTasks[task.status].tasks.push(task);
        } else if (task.status) {
          // Если статус не найден в списке, но есть в задаче
          // создаем новую группу для этого статуса
          groupedTasks[task.status] = {
            status: { _id: task.status, name: "Неизвестный статус", color: "#CCCCCC" },
            tasks: [task]
          };
        }
      });
    }

    return Object.values(groupedTasks);
  };

  return (
    <div className="task-board-container">
      <div className="task-board-header">
        <h1 className="task-board-title">Задачи</h1>
        <Link to="/tasks/new" className="btn btn-primary add-task-btn">
          <i className="fas fa-plus"></i> Добавить задачу
        </Link>
      </div>

      <div className="task-filters">
        <div className="filter-row">
          <div className="search-box">
            <i className="fas fa-search"></i>
            <input
              type="text"
              placeholder="Поиск задач..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </div>

          <div className="filter-group">
            <select 
              className="form-control"
              value={filterCategory}
              onChange={e => setFilterCategory(e.target.value)}
            >
              <option value="all">Все категории</option>
              <option value="development">Разработка</option>
              <option value="design">Дизайн</option>
              <option value="marketing">Маркетинг</option>
            </select>
          </div>

          <div className="filter-group">
            <select 
              className="form-control"
              value={sortBy}
              onChange={e => setSortBy(e.target.value)}
            >
              <option value="deadline">По сроку</option>
              <option value="priority">По приоритету</option>
              <option value="created">По дате создания</option>
            </select>
          </div>
        </div>
      </div>

      {!tasks || tasks.length === 0 ? (
        <div className="empty-state">
          <div className="empty-state-icon">
            <i className="fas fa-tasks"></i>
          </div>
          <h3 className="empty-state-title">Нет задач</h3>
          <p className="empty-state-description">Нажмите "Добавить задачу", чтобы создать новую задачу</p>
        </div>
      ) : (
        <div className="task-columns">
          {groupTasksByStatus().map(group => (
            <div 
              key={group.status._id} 
              className="task-column"
            >
              <div className="column-header" style={{ borderBottomColor: group.status.color || 'var(--primary-color)' }}>
                <div className="column-title">
                  <i className="fas fa-tasks" style={{ color: group.status.color || 'var(--primary-color)' }}></i>
                  {group.status.name}
                </div>
                <span 
                  className="task-count"
                  style={{ backgroundColor: group.status.color || 'var(--primary-color)' }}
                >
                  {group.tasks.length}
                </span>
              </div>

              {group.tasks.length === 0 ? (
                <div className="empty-column">
                  Нет задач в этом статусе
                </div>
              ) : (
                <div className="tasks-grid">
                  {group.tasks.map(task => (
                    <Link key={task._id} to={`/tasks/${task._id}`} className="task-link">
                      <div className={`task-card ${getPriorityClass(task.priority)}`}>
                        <h3 className="task-title">{task.title}</h3>
                        {task.description && (
                          <p className="task-description">
                            {task.description.length > 100
                              ? `${task.description.substring(0, 100)}...`
                              : task.description}
                          </p>
                        )}
                        <div className="task-meta">
                          <div className="task-info">
                            {task.dueDate && (
                              <div className="task-due-date">
                                <i className="far fa-calendar-alt"></i>
                                {formatDate(task.dueDate)}
                              </div>
                            )}
                          </div>
                          {task.assignedTo && (
                            <div className="task-assignee">
                              <i className="far fa-user"></i>
                              {typeof task.assignedTo === 'object' ? task.assignedTo.name : 'Назначено'}
                            </div>
                          )}
                        </div>
                      </div>
                    </Link>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// Вспомогательные функции
const formatDate = (dateString) => {
  if (!dateString) return '';
  const date = new Date(dateString);
  return date.toLocaleDateString('ru-RU');
};

const getPriorityClass = (priority) => {
  switch (priority) {
    case 'high':
      return 'priority-high';
    case 'medium':
      return 'priority-medium';
    case 'low':
      return 'priority-low';
    default:
      return '';
  }
};

export default TaskList;
