// client/src/pages/TaskBoard.js
import React, { useContext, useEffect, useState } from 'react';
import { StatusContext } from '../context/StatusContext';
import { SettingsContext } from '../context/SettingsContext';
import TaskList from '../components/tasks/TaskList';
import TaskForm from '../components/tasks/TaskForm';
import Spinner from '../components/layout/Spinner';

const TaskBoard = () => {
  const statusContext = useContext(StatusContext);
  const settingsContext = useContext(SettingsContext);
  const { loading: statusLoading, getStatuses } = statusContext;
  const { loading: settingsLoading, getSettings } = settingsContext;
  const [showForm, setShowForm] = useState(false);

  useEffect(() => {
    getStatuses();
    getSettings();
    // eslint-disable-next-line
  }, []);

  const toggleForm = () => {
    setShowForm(!showForm);
  };

  const handleTaskAdded = () => {
    setShowForm(false);
  };

  if (statusLoading || settingsLoading) {
    return <Spinner />;
  }

  return (
    <div className="task-board-page">
      <div className="page-header">
        <h2>Доска задач</h2>
        <button onClick={toggleForm} className="btn btn-primary">
          {showForm ? 'Скрыть форму' : 'Добавить задачу'}
        </button>
      </div>

      {showForm && <TaskForm onTaskAdded={handleTaskAdded} />}

      <TaskList />
    </div>
  );
};

export default TaskBoard;
