// client/src/components/tasks/TaskList.js
import React, { useContext, useEffect, useState } from 'react';
import { TaskContext } from '../../context/TaskContext';
import { StatusContext } from '../../context/StatusContext';
import TaskItem from './TaskItem';
import Spinner from '../layout/Spinner';

const TaskList = () => {
  const taskContext = useContext(TaskContext);
  const statusContext = useContext(StatusContext);

  const { tasks, loading, getTasks } = taskContext;
  const { statuses, getStatuses } = statusContext;

  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');
  const [sortBy, setSortBy] = useState('deadline');
  const [viewMode, setViewMode] = useState('kanban'); // kanban, list, table

  // Загрузка данных при монтировании
  useEffect(() => {
    getTasks();
    getStatuses();
    // eslint-disable-next-line
  }, []);

  // Периодическое обновление каждые 30 секунд
  useEffect(() => {
    const intervalId = setInterval(() => {
      getTasks();
    }, 30000);

    return () => clearInterval(intervalId);
    // eslint-disable-next-line
  }, []);

  if (loading) {
    return <Spinner />;
  }

  // Группировка задач по статусам для канбан-доски
  const groupTasksByStatus = () => {
    const groupedTasks = {};

    // Инициализация групп для всех статусов
    if (statuses) {
      statuses.forEach(status => {
        groupedTasks[status._id] = {
          status,
          tasks: []
        };
      });

      // Фильтрация и сортировка
      let filteredTasks = tasks || [];

      // Поиск
      if (searchTerm) {
        const term = searchTerm.toLowerCase();
        filteredTasks = filteredTasks.filter(task => 
          (task.title && task.title.toLowerCase().includes(term)) || 
          (task.description && task.description.toLowerCase().includes(term))
        );
      }

      // Фильтрация по категории
      if (filterCategory !== 'all') {
        filteredTasks = filteredTasks.filter(task => 
          task.category === filterCategory
        );
      }

      // Сортировка
      switch (sortBy) {
        case 'deadline':
          filteredTasks.sort((a, b) => {
            if (!a.dueDate) return 1;
            if (!b.dueDate) return -1;
            return new Date(a.dueDate) - new Date(b.dueDate);
          });
          break;
        case 'priority':
          const priorityOrder = { high: 1, medium: 2, low: 3 };
          filteredTasks.sort((a, b) => 
            priorityOrder[a.priority || 'medium'] - priorityOrder[b.priority || 'medium']
          );
          break;
        case 'created':
          filteredTasks.sort((a, b) => 
            new Date(b.createdAt) - new Date(a.createdAt)
          );
          break;
        default:
          break;
      }

      // Распределение задач по группам
      filteredTasks.forEach(task => {
        if (task.status && groupedTasks[task.status._id]) {
          groupedTasks[task.status._id].tasks.push(task);
        } else if (task.status) {
          groupedTasks[task.status._id] = {
            status: task.status,
            tasks: [task]
          };
        }
      });
    }

    return Object.values(groupedTasks);
  };

  // Рендеринг в стиле канбан-доски
  const renderKanbanBoard = () => {
    const statusGroups = groupTasksByStatus();

    return (
      <div className="kanban-board">
        {statusGroups.map(group => (
          <div key={group.status._id} className="kanban-column">
            <div className="kanban-column-header">
              <div className="kanban-column-title">
                <div 
                  className="kanban-column-title-icon"
                  style={{ backgroundColor: group.status.color || '#3498db' }}
                ></div>
                {group.status.name}
                <span className="kanban-count">{group.tasks.length}</span>
              </div>
            </div>
            <div className="kanban-column-body">
              {group.tasks.length === 0 ? (
                <div className="empty-message">No tasks</div>
              ) : (
                group.tasks.map(task => (
                  <TaskItem key={task._id} task={task} />
                ))
              )}
              <div className="kanban-add">
                <i className="fas fa-plus"></i>
              </div>
            </div>
          </div>
        ))}
      </div>
    );
  };

  return (
    <div className="task-board-container">
      <div className="task-board-header">
        <div className="view-controls">
          <div className="view-modes">
            <button 
              className={`view-mode-btn ${viewMode === 'table' ? 'active' : ''}`}
              onClick={() => setViewMode('table')}
            >
              <i className="fas fa-table"></i>
              <span>Table</span>
            </button>
            <button 
              className={`view-mode-btn ${viewMode === 'list' ? 'active' : ''}`}
              onClick={() => setViewMode('list')}
            >
              <i className="fas fa-list"></i>
              <span>List</span>
            </button>
            <button 
              className={`view-mode-btn ${viewMode === 'kanban' ? 'active' : ''}`}
              onClick={() => setViewMode('kanban')}
            >
              <i className="fas fa-columns"></i>
              <span>Kanban</span>
            </button>
          </div>
        </div>

        <div className="task-board-actions">
          <div className="search-container">
            <i className="fas fa-search search-icon"></i>
            <input
              type="text"
              className="search-input"
              placeholder="Search tasks..."
              value={searchTerm}
              onChange={e => setSearchTerm(e.target.value)}
            />
          </div>

          <div className="filter-sort">
            <div className="dropdown">
              <button className="btn btn-secondary">
                <i className="fas fa-filter"></i>
                <span>Filter</span>
              </button>
            </div>

            <div className="dropdown">
              <button className="btn btn-secondary">
                <i className="fas fa-sort"></i>
                <span>Sort: {sortBy === 'deadline' ? 'Deadline' : sortBy === 'priority' ? 'Priority' : 'Created'}</span>
              </button>
            </div>
          </div>

          <button className="btn btn-add">
            <i className="fas fa-plus"></i>
            <span>New Task</span>
          </button>
        </div>
      </div>

      {!tasks || tasks.length === 0 ? (
        <div className="empty-board">
          <div className="empty-board-icon">
            <i className="fas fa-clipboard-list"></i>
          </div>
          <h3 className="empty-board-title">No Tasks Yet</h3>
          <p className="empty-board-text">Create your first task to get started</p>
          <button className="btn btn-primary">
            <i className="fas fa-plus"></i>
            <span>Create Task</span>
          </button>
        </div>
      ) : (
        viewMode === 'kanban' && renderKanbanBoard()
      )}
    </div>
  );
};

export default TaskList;
