// client/src/components/layout/ThemeToggle.js
import React, { useContext, useState, useEffect, useRef } from 'react';
import { SettingsContext } from '../../context/SettingsContext';
import './ThemeToggle.css';

const ThemeToggle = ({ collapsed = false }) => {
  const settingsContext = useContext(SettingsContext);
  const { theme, toggleTheme } = settingsContext;
  const [expanded, setExpanded] = useState(false);
  const [isHovering, setIsHovering] = useState(false);
  const nodeRef = useRef(null);
  const popupRef = useRef(null);

  const toggleExpand = () => {
    if (!collapsed) {
      setExpanded(!expanded);
    } else {
      setIsHovering(!isHovering); // В свернутом режиме переключаем видимость всплывающего окна
    }
  };

  // Закрываем всплывающее окно при клике вне элемента
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (nodeRef.current && !nodeRef.current.contains(event.target)) {
        setExpanded(false);
        setIsHovering(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Вместо событий наведения используем также нажатие для отображения всплывающего окна
  return (
    <div 
      className={`theme-menu-item ${collapsed ? 'collapsed' : ''}`} 
      ref={nodeRef}
    >
      <div 
        className="theme-menu-header" 
        onClick={toggleExpand}
      >
        <div className="theme-menu-icon">
          <i className="fas fa-lightbulb"></i>
        </div>
        {!collapsed && <span className="theme-menu-text">Theme</span>}
        {!collapsed && <i className={`fas ${expanded ? 'fa-chevron-up' : 'fa-chevron-down'} theme-menu-arrow`}></i>}
      </div>

      {/* Нормальное подменю для развернутой панели */}
      {!collapsed && expanded && (
        <div className="theme-submenu">
          <div className="theme-option">
            <span>Dark</span>
            <label className="theme-switch">
              <input 
                type="checkbox" 
                checked={theme === 'dark'} 
                onChange={toggleTheme} 
              />
              <span className="theme-slider"></span>
            </label>
          </div>
        </div>
      )}

      {/* Всплывающее меню для свернутой панели */}
      {collapsed && isHovering && (
        <div 
          className="theme-hover-popup" 
          ref={popupRef}
          onClick={(e) => e.stopPropagation()}
        >
          <div className="theme-popup-header">Theme</div>
          <div className="theme-popup-content">
            <div className="theme-option">
              <span>Dark</span>
              <label className="theme-switch">
                <input 
                  type="checkbox" 
                  checked={theme === 'dark'} 
                  onChange={(e) => {
                    e.stopPropagation(); 
                    toggleTheme();
                  }}
                />
                <span className="theme-slider"></span>
              </label>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ThemeToggle;
