// client/src/components/tasks/TaskItem.js
import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import moment from 'moment';
import { TaskContext } from '../../context/TaskContext';
import { StatusContext } from '../../context/StatusContext';
import 'moment/locale/ru';

moment.locale('ru');

const TaskItem = ({ task }) => {
  const taskContext = useContext(TaskContext);
  const statusContext = useContext(StatusContext);
  const { updateTask } = taskContext;
  const { statuses } = statusContext;

  // Функция для изменения статуса задачи
  const handleStatusChange = async (statusId) => {
    try {
      await updateTask(task._id, { status: statusId });
    } catch (err) {
      console.error('Ошибка при изменении статуса:', err);
    }
  };

  // Получение класса для приоритета
  const getPriorityClass = () => {
    switch (task.priority) {
      case 'high':
        return 'task-priority-high';
      case 'medium':
        return 'task-priority-medium';
      case 'low':
        return 'task-priority-low';
      default:
        return 'task-priority-medium';
    }
  };

  // Получение метки приоритета
  const getPriorityLabel = () => {
    switch (task.priority) {
      case 'high':
        return 'High';
      case 'medium':
        return 'Medium';
      case 'low':
        return 'Low';
      default:
        return 'Medium';
    }
  };

  // Получение инициалов пользователя
  const getUserInitials = (name) => {
    if (!name) return '';
    return name
      .split(' ')
      .map(part => part[0])
      .join('')
      .toUpperCase()
      .substring(0, 2);
  };

  // Форматирование времени выполнения
  const formatTimeLeft = (dueDate) => {
    if (!dueDate) return null;

    const now = moment();
    const due = moment(dueDate);
    const diff = due.diff(now, 'days');

    if (diff < 0) {
      return 'Просрочено';
    } else if (diff === 0) {
      return 'Сегодня';
    } else if (diff === 1) {
      return '1 день';
    } else {
      return `${diff} дней`;
    }
  };

  // Обрезание длинного текста с добавлением многоточия
  const truncateText = (text, maxLength = 80) => {
    if (!text) return '';
    if (text.length <= maxLength) return text;
    return text.substring(0, maxLength) + '...';
  };

  return (
    <div className="task-card">
      <div className="task-card-header">
        <div className="task-badges">
          <span className={`task-badge ${getPriorityClass()}`}>
            {getPriorityLabel()}
          </span>
        </div>
        <h3 className="task-title">{task.title}</h3>
        {task.description && (
          <p className="task-description">{truncateText(task.description)}</p>
        )}
      </div>

      <div className="task-footer">
        <div className="task-avatars">
          {task.createdBy && (
            <img 
              src={task.createdBy.avatar || `https://ui-avatars.com/api/?name=${encodeURIComponent(task.createdBy.name)}&background=random`} 
              alt={task.createdBy.name} 
              className="task-avatar" 
              title={task.createdBy.name}
            />
          )}
        </div>

        <div className="task-meta-actions">
          {task.dueDate && (
            <div className="task-due-date">
              <i className="far fa-clock"></i>
              <span>{formatTimeLeft(task.dueDate)}</span>
            </div>
          )}

          {task.comments && task.comments.length > 0 && (
            <div className="task-comments">
              <i className="far fa-comment"></i>
              <span>{task.comments.length}</span>
            </div>
          )}

          <button className="task-more">
            <i className="fas fa-ellipsis-h"></i>
          </button>
        </div>
      </div>
    </div>
  );
};

export default TaskItem;
