// client/src/components/tasks/TaskItem.js
import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import moment from 'moment';
import { TaskContext } from '../../context/TaskContext';
import { StatusContext } from '../../context/StatusContext';
import 'moment/locale/ru';

moment.locale('ru');

const TaskItem = ({ task }) => {
  const taskContext = useContext(TaskContext);
  const statusContext = useContext(StatusContext);
  const { updateTask } = taskContext;
  const { statuses } = statusContext;

  const getStatusStyle = () => {
    return {
      backgroundColor: task.status.color || '#3498db',
      color: '#fff',
      padding: '3px 8px',
      borderRadius: '3px',
      fontSize: '0.8rem'
    };
  };

  const getPriorityStyle = () => {
    switch (task.priority) {
      case 'high':
        return { color: '#e74c3c' };
      case 'medium':
        return { color: '#f39c12' };
      case 'low':
        return { color: '#2ecc71' };
      default:
        return {};
    }
  };

  const getPriorityLabel = () => {
    switch (task.priority) {
      case 'high':
        return 'Высокий';
      case 'medium':
        return 'Средний';
      case 'low':
        return 'Низкий';
      default:
        return '';
    }
  };

  // Функция для изменения статуса задачи
  const handleStatusChange = async (statusId) => {
    try {
      await updateTask(task._id, { status: statusId });
    } catch (err) {
      console.error('Ошибка при изменении статуса:', err);
    }
  };

  return (
    <div className="task-item">
      <div className="task-header">
        <h3>
          <Link to={`/tasks/${task._id}`}>{task.title}</Link>
        </h3>
        <div className="task-status-dropdown">
          <span style={getStatusStyle()} className="status-label">
            {task.status.name}
          </span>
          <div className="status-dropdown-content">
            {statuses && statuses.map(status => (
              <div
                key={status._id}
                onClick={() => handleStatusChange(status._id)}
                className="status-option"
                style={{ backgroundColor: status.color || '#3498db', color: '#fff' }}
              >
                {status.name}
              </div>
            ))}
          </div>
        </div>
      </div>
      <div className="task-body">
        {task.description && (
          <p className="description">{task.description.length > 100 ? `${task.description.substring(0, 100)}...` : task.description}</p>
        )}
      </div>
      <div className="task-footer">
        <div className="task-meta">
          <div className="priority">
            <span style={getPriorityStyle()}>
              <i className="fas fa-flag"></i> {getPriorityLabel()}
            </span>
          </div>
          <div className="created-by">
            <i className="fas fa-user"></i> {task.createdBy && task.createdBy.name}
          </div>
          <div className="created-at">
            <i className="far fa-calendar-alt"></i> {moment(task.createdAt).format('LL')}
          </div>
          {task.dueDate && (
            <div className="due-date">
              <i className="fas fa-hourglass-end"></i> {moment(task.dueDate).format('LL')}
            </div>
          )}
          {task.attachments && task.attachments.length > 0 && (
            <div className="attachments">
              <i className="fas fa-paperclip"></i> {task.attachments.length}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default TaskItem;
