// client/src/components/layout/Navbar.js
import React, { useContext, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { SettingsContext } from '../../context/SettingsContext';

const Navbar = () => {
  const authContext = useContext(AuthContext);
  const settingsContext = useContext(SettingsContext);

  const { isAuthenticated, user, logout } = authContext;
  const { settings, getSettings } = settingsContext;

  useEffect(() => {
    if (isAuthenticated) {
      getSettings();
    }
    // eslint-disable-next-line
  }, [isAuthenticated]);

  const onLogout = () => {
    logout();
  };

  const getDate = () => {
    const options = { weekday: 'long', month: 'long', day: 'numeric' };
    return new Date().toLocaleDateString('ru-RU', options);
  };

  const authLinks = (
    <div className="navbar-right">
      <div className="navbar-date">
        <i className="far fa-calendar-alt navbar-date-icon"></i>
        <span>{getDate()}</span>
      </div>
      <div className="navbar-actions">
        <button className="btn-icon">
          <i className="fas fa-bell"></i>
        </button>
        <button className="btn-icon">
          <i className="fas fa-cog"></i>
        </button>
        <div className="navbar-user">
          {user && (
            <img
              src={user.avatar || `https://ui-avatars.com/api/?name=${encodeURIComponent(user.name)}&background=random`}
              alt={user.name}
              className="user-avatar"
            />
          )}
        </div>
      </div>
    </div>
  );

  const guestLinks = (
    <div className="navbar-right">
      <Link to="/register" className="navbar-item">Регистрация</Link>
      <Link to="/login" className="navbar-item">Вход</Link>
    </div>
  );

  return (
    <nav className="navbar">
      <div className="navbar-left">
        <div className="navbar-breadcrumb">
          <Link to="/" className="navbar-breadcrumb-item">Проекты</Link>
          <span className="navbar-breadcrumb-separator">/</span>
          <span className="navbar-breadcrumb-item">Все задачи</span>
        </div>
        <h1 className="navbar-title">
          {settings && settings.companyName ? settings.companyName : 'Доска задач'}
        </h1>
      </div>
      {isAuthenticated ? authLinks : guestLinks}
    </nav>
  );
};

export default Navbar;
