// client/src/pages/TaskBoard.js
import React, { useContext, useEffect, useState } from 'react';
import { StatusContext } from '../context/StatusContext';
import { SettingsContext } from '../context/SettingsContext';
import { TaskContext } from '../context/TaskContext';
import TaskList from '../components/tasks/TaskList';
import TaskForm from '../components/tasks/TaskForm';
import Spinner from '../components/layout/Spinner';
import './TaskBoard.css';

const TaskBoard = () => {
  const statusContext = useContext(StatusContext);
  const settingsContext = useContext(SettingsContext);
  const taskContext = useContext(TaskContext);

  const { loading: statusLoading, statuses, getStatuses } = statusContext;
  const { loading: settingsLoading, getSettings } = settingsContext;
  const { tasks, getTasks } = taskContext;

  const [showForm, setShowForm] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');

  // Доступные категории (можно вынести в настройки позже)
  const categories = [
    { id: 'all', name: 'Все категории' },
    { id: 'development', name: 'Разработка' },
    { id: 'design', name: 'Дизайн' },
    { id: 'marketing', name: 'Маркетинг' }
  ];

  useEffect(() => {
    getStatuses();
    getSettings();
    getTasks();
    // eslint-disable-next-line
  }, []);

  const toggleForm = () => {
    setShowForm(!showForm);
  };

  const handleTaskAdded = () => {
    setShowForm(false);
    getTasks(); // Обновление списка задач
  };

  const handleSearchChange = (e) => {
    setSearchTerm(e.target.value);
  };

  const handleCategoryChange = (e) => {
    setFilterCategory(e.target.value);
  };

  if (statusLoading || settingsLoading) {
    return <Spinner />;
  }

  return (
    <div className="task-board-container">
      <div className="task-board-header">
        <h1 className="task-board-title">Доска задач</h1>
        <button onClick={toggleForm} className="btn btn-primary add-task-btn">
          <i className="fas fa-plus"></i> {showForm ? 'Скрыть форму' : 'Добавить задачу'}
        </button>
      </div>

      {showForm && (
        <div className="task-form-container">
          <TaskForm onTaskAdded={handleTaskAdded} />
        </div>
      )}

      <div className="task-filters">
        <div className="filter-row">
          <div className="search-box">
            <i className="fas fa-search"></i>
            <input
              type="text"
              placeholder="Поиск задач..."
              value={searchTerm}
              onChange={handleSearchChange}
            />
          </div>

          <div className="filter-group">
            <select 
              className="form-control"
              value={filterCategory}
              onChange={handleCategoryChange}
            >
              {categories.map(category => (
                <option key={category.id} value={category.id}>
                  {category.name}
                </option>
              ))}
            </select>
          </div>
        </div>
      </div>

      {/* Передача параметров и функции обновления в TaskList */}
      <TaskList 
        searchTerm={searchTerm} 
        filterCategory={filterCategory} 
        categories={categories}
        onAddTask={toggleForm}
      />
    </div>
  );
};

export default TaskBoard;
