// client/src/components/tasks/TaskItem.js
import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import moment from 'moment';
import { TaskContext } from '../../context/TaskContext';
import { StatusContext } from '../../context/StatusContext';
import 'moment/locale/ru';

moment.locale('ru');

const TaskItem = ({ task }) => {
  const taskContext = useContext(TaskContext);
  const statusContext = useContext(StatusContext);
  const { updateTask } = taskContext;
  const { statuses } = statusContext;

  // Функция для изменения статуса задачи
  const handleStatusChange = async (statusId) => {
    try {
      await updateTask(task._id, { status: statusId });
    } catch (err) {
      console.error('Ошибка при изменении статуса:', err);
    }
  };

  // Получение класса для приоритета
  const getPriorityClass = () => {
    switch (task.priority) {
      case 'high':
        return 'priority-high';
      case 'medium':
        return 'priority-medium';
      case 'low':
        return 'priority-low';
      default:
        return 'priority-medium';
    }
  };

  // Получение метки приоритета
  const getPriorityLabel = () => {
    switch (task.priority) {
      case 'high':
        return 'Высокий';
      case 'medium':
        return 'Средний';
      case 'low':
        return 'Низкий';
      default:
        return 'Средний';
    }
  };

  // Получение инициалов пользователя
  const getUserInitials = (name) => {
    if (!name) return '';
    return name
      .split(' ')
      .map(part => part[0])
      .join('')
      .toUpperCase()
      .substring(0, 2);
  };

  return (
    <div className="task-card">
      <div className="task-card-header">
        <Link to={`/tasks/${task._id}`} className="task-title">
          {task.title}
        </Link>
        <div className="task-status-dropdown">
          <div className="status-badge" style={{ backgroundColor: task.status.color || '#3498db' }}>
            {task.status.name}
          </div>
          <div className="status-dropdown-content">
            {statuses && statuses.map(status => (
              <div
                key={status._id}
                onClick={() => handleStatusChange(status._id)}
                className="status-option"
                style={{ backgroundColor: status.color || '#3498db' }}
              >
                {status.name}
              </div>
            ))}
          </div>
        </div>
      </div>

      {task.description && (
        <div className="task-description">
          {task.description.length > 100 
            ? `${task.description.substring(0, 100)}...` 
            : task.description}
        </div>
      )}

      <div className="task-meta">
        <div className={`task-priority ${getPriorityClass()}`}>
          <i className="fas fa-flag"></i> {getPriorityLabel()}
        </div>

        <div className="task-info-row">
          {task.createdBy && (
            <div className="task-created-by">
              <div className="user-avatar">
                {getUserInitials(task.createdBy.name)}
              </div>
              <span className="user-name">{task.createdBy.name}</span>
            </div>
          )}

          <div className="task-dates">
            <div className="task-date">
              <i className="far fa-calendar-alt"></i>
              <span>{moment(task.createdAt).format('DD MMM YYYY')}</span>
            </div>

            {task.dueDate && (
              <div className="task-due-date">
                <i className="fas fa-hourglass-end"></i>
                <span>{moment(task.dueDate).format('DD MMM YYYY')}</span>
              </div>
            )}
          </div>
        </div>

        {task.attachments && task.attachments.length > 0 && (
          <div className="task-attachments">
            <i className="fas fa-paperclip"></i>
            <span>{task.attachments.length} файл(ов)</span>
          </div>
        )}
      </div>
    </div>
  );
};

export default TaskItem;
