// client/src/components/layout/ThemeToggle.js
import React, { useContext, useState, useEffect, useRef } from 'react';
import { SettingsContext } from '../../context/SettingsContext';
import './ThemeToggle.css';

const ThemeToggle = ({ collapsed }) => {
  const settingsContext = useContext(SettingsContext);
  const { theme, toggleTheme } = settingsContext;
  const [showMenu, setShowMenu] = useState(false);
  const menuRef = useRef(null);

  // Закрывать меню при клике вне компонента
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (menuRef.current && !menuRef.current.contains(event.target)) {
        setShowMenu(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const toggleMenu = () => {
    setShowMenu(!showMenu);
  };

  return (
    <div ref={menuRef} className="theme-toggle-container">
      <button className="theme-toggle-button" onClick={toggleMenu}>
        <i className="fas fa-lightbulb"></i>
      </button>
      {showMenu && (
        <div className={`theme-dropdown ${collapsed ? 'theme-dropdown-collapsed' : ''}`}>
          <div className="theme-dropdown-header">
            <span>Theme</span>
          </div>
          <div className="theme-dropdown-content">
            <div className="theme-option">
              <span>Dark</span>
              <label className="theme-switch">
                <input 
                  type="checkbox" 
                  checked={theme === 'dark'} 
                  onChange={toggleTheme} 
                />
                <span className="theme-slider"></span>
              </label>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default ThemeToggle;
