// client/src/components/layout/Navbar.js
import React, { useContext, useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { AuthContext } from '../../context/AuthContext';
import { SettingsContext } from '../../context/SettingsContext';

const Navbar = () => {
  const authContext = useContext(AuthContext);
  const settingsContext = useContext(SettingsContext);
  const location = useLocation();
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  const { isAuthenticated, user, logout } = authContext;
  const { settings, getSettings } = settingsContext;

  useEffect(() => {
    if (isAuthenticated) {
      getSettings();
    }
    // eslint-disable-next-line
  }, [isAuthenticated]);

  const onLogout = () => {
    logout();
  };

  const toggleMobileMenu = () => {
    setIsMobileMenuOpen(!isMobileMenuOpen);
  };

  const closeMobileMenu = () => {
    setIsMobileMenuOpen(false);
  };

  const isActive = (path) => {
    return location.pathname === path ? 'active' : '';
  };

  const authLinks = (
    <ul className={`nav-menu ${isMobileMenuOpen ? 'show' : ''}`}>
      <li className={isActive('/tasks')}>
        <Link to="/tasks" onClick={closeMobileMenu}>
          <i className="fas fa-tasks"></i> Задачи
        </Link>
      </li>
      <li className={isActive('/calendar')}>
        <Link to="/calendar" onClick={closeMobileMenu}>
          <i className="fas fa-calendar-alt"></i> Календарь
        </Link>
      </li>
      <li className={isActive('/reports')}>
        <Link to="/reports" onClick={closeMobileMenu}>
          <i className="fas fa-chart-bar"></i> Отчеты
        </Link>
      </li>
      <li className={isActive('/clients')}>
        <Link to="/clients" onClick={closeMobileMenu}>
          <i className="fas fa-users"></i> Клиенты
        </Link>
      </li>
      {user && user.role === 'admin' && (
        <>
          <li className={isActive('/users')}>
            <Link to="/users" onClick={closeMobileMenu}>
              <i className="fas fa-user-cog"></i> Пользователи
            </Link>
          </li>
          <li className={isActive('/settings')}>
            <Link to="/settings" onClick={closeMobileMenu}>
              <i className="fas fa-cog"></i> Настройки
            </Link>
          </li>
        </>
      )}
      <li>
        <a onClick={onLogout} href="#!" className="logout-btn">
          <i className="fas fa-sign-out-alt"></i> <span>Выход</span>
        </a>
      </li>
    </ul>
  );

  const guestLinks = (
    <ul className={`nav-menu ${isMobileMenuOpen ? 'show' : ''}`}>
      <li className={isActive('/register')}>
        <Link to="/register" onClick={closeMobileMenu}>
          <i className="fas fa-user-plus"></i> Регистрация
        </Link>
      </li>
      <li className={isActive('/login')}>
        <Link to="/login" onClick={closeMobileMenu}>
          <i className="fas fa-sign-in-alt"></i> Вход
        </Link>
      </li>
    </ul>
  );

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="navbar-logo" onClick={closeMobileMenu}>
          {settings && settings.companyName ? settings.companyName : 'Система управления производством'}
        </Link>
        <div className="menu-icon" onClick={toggleMobileMenu}>
          <i className={isMobileMenuOpen ? 'fas fa-times' : 'fas fa-bars'} />
        </div>
        {isAuthenticated ? authLinks : guestLinks}
      </div>
    </nav>
  );
};

export default Navbar;
